function ransackConfig(){
	$('ransack-select').on('change', function(){
		val = $(this).val();
		elem = $(this);
		all_values = '';


		if (val == 'is_resident') $(elem).parent().find('.predicate').html('<option value="eq">igual</option>') ;
		else{
			$(elem).parent().find('.predicate').html('<option value="cont">contém</option>') ;
			$(elem).parent().find('.predicate').append('<option value="eq">igual</option>') ;
			$(elem).parent().find('.predicate').append('<option value="start">começa em</option>') ;
		}


		jQuery.ajax({
			url: "/registro_academico/relatorios/model_identification",
			type: "GET",
			data: {
				field: val,
				model_object: $(this).attr('class')
			},
			dataType: "html",
			success: function(data) {
				id = $(elem).parent().find('.ransack-values').children().attr('id')
				name = $(elem).parent().find('.ransack-values').children().attr('name')

				field_type = ''
				if (data.length != 1){
					class_name = $.parseJSON(data)[0].field_class_name;
					field_type = "<select id='" + id + "' name='" + name + "' class='" + class_name + "' ></select>";
					$(elem).parent().find('.ransack-values').html(field_type);

					$.each($.parseJSON(data),function(key, value){
						if (value.field_show == '')
							$("#"+id+"."+class_name).append('<option value="' + value.field_value + '"">' + value.field_value + '</option>');
						else
							$("#"+id+"."+class_name).append('<option value="' + value.field_value + '"">' + value.field_show + '</option>');

					});
				}else{

					field_type = "<input id='" + id + "' name='" + name + "'></input>";
					$(elem).parent().find('.ransack-values').html(field_type);
				}

			},
			error: function(){
				alert('Houve um erro. Tente novamente mais tarde.')
			}
	  	});
	});

	// retorna os valores dos cursos do campus selecionado
	$('#dept').on('change', function(){
	    var dept_id = $('#dept').val();
	    if(dept_id == "") dept_id = '0';
		jQuery.ajax({
			url: "/registro_academico/relatorios/select_dept/" + dept_id,
			type: "GET",
			dataType: "html",
			success: function(data) {
				$("#course").empty();
				$("#course").append('<option value="">TODOS</option>');
 				$.each($.parseJSON(data),function(key, value){
					$("#course").append('<option value="' + value.id + '"">' +  value.name + '</option>');
				});
			},
			error: function(){
				alert('Houve um erro. Tente novamente mais tarde.')
			}
	  	});
	});
}

